import React, { useState } from 'react';

interface Grade {
  id: number;
  name: string;
  facultyNumber: string;
  subject: string;
  grade: number;
}

interface HistoryItem {
  timestamp: string;
  action: string;
  grade: Grade;
}

const GradeManagementSystem: React.FC = () => {
  const [grades, setGrades] = useState<Grade[]>([]);
  const [field1, setField1] = useState('');
  const [field2, setField2] = useState('');
  const [field3, setField3] = useState('');
  const [field4, setField4] = useState<number | ''>('');
  const [selectedGrade, setSelectedGrade] = useState<Grade | null>(null);
  const [history, setHistory] = useState<HistoryItem[]>([]);

  const handleNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setField1(e.target.value);
  };

  const handleFacultyNumberChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setField2(e.target.value);
  };

  const handleSubjectChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setField3(e.target.value);
  };

  const handleGradeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setField4(e.target.valueAsNumber);
  };

  const saveButton = () => {
    const newGrade: Grade = {
      id: Date.now(),
      name: field1,
      facultyNumber: field2,
      subject: field3,
      grade: field4,
    };

    setGrades([...grades, newGrade]);

    const newHistoryItem: HistoryItem = {
      timestamp: getCurrentTimestamp(),
      action: 'Added',
      grade: newGrade,
    };
    setHistory([...history, newHistoryItem]);

    setField1('');
    setField2('');
    setField3('');
    setField4('');
  };

  const deleteButton = (id: number) => {
    const deletedGrade = grades.find((grade) => grade.id === id);
    if (deletedGrade) {
      const updatedGrades = grades.filter((grade) => grade.id !== id);
      setGrades(updatedGrades);

      const newHistoryItem: HistoryItem = {
        timestamp: getCurrentTimestamp(),
        action: 'Deleted',
        grade: deletedGrade,
      };
      setHistory([...history, newHistoryItem]);

      if (selectedGrade && selectedGrade.id === id) {
        setSelectedGrade(null);
      }
    }
  };

  const handleGradeClick = (grade: Grade) => {
    setSelectedGrade(grade);
  };

  const clearButton = () => {
    setSelectedGrade(null);
  };

  const getCurrentTimestamp = () => {
    const currentDate = new Date();
    const timestamp = currentDate.toLocaleString();
    return timestamp;
  };

  return (
    <div>
      <nav>
        <h1>Grade Management System</h1>
      </nav>

      <section className="list-section">
        <div className="container">
          <div className="input-group">
            <label htmlFor="nameInput">Name:</label>
            <input
              type="text"
              placeholder="Enter Name"
              id="nameInput"
              value={field1}
              onChange={handleNameChange}
            />
          </div>

          <div className="input-group">
            <label htmlFor="facultyNumberInput">Faculty Number:</label>
            <input
              type="text"
              placeholder="Enter Faculty Number"
              id="facultyNumberInput"
              value={field2}
              onChange={handleFacultyNumberChange}
            />
          </div>

          <div className="input-group">
            <label htmlFor="subjectInput">Subject:</label>
            <input
              type="text"
              placeholder="Enter Subject"
              id="subjectInput"
              value={field3}
              onChange={handleSubjectChange}
            />
          </div>

          <div className="input-group">
            <label htmlFor="gradeInput">Grade:</label>
            <input
              type="number"
              placeholder="Enter Grade"
              id="gradeInput"
              value={field4 === '' ? '' : field4}
              onChange={handleGradeChange}
            />
          </div>

          <button id="saveButton" onClick={saveButton}>
            Save
          </button>

          <ul className="grade-list">
            {grades.map((grade) => (
              <li
                key={grade.id}
                className={`grade-item ${selectedGrade?.id === grade.id ? 'selected' : ''}`}
                onClick={() => handleGradeClick(grade)}
              >
                <span>{grade.name} ({grade.facultyNumber}): </span>
                <span>{grade.subject} - {grade.grade}</span>
                <button className="deleteButton" onClick={() => deleteButton(grade.id)}>
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>
      </section>

      <section className="content-details">
        <div className="container">
          <h2>Details</h2>
          {selectedGrade ? (
            <div>
              <p>Name: {selectedGrade.name}</p>
              <p>Faculty Number: {selectedGrade.facultyNumber}</p>
              <p>Subject: {selectedGrade.subject}</p>
              <p>Grade: {selectedGrade.grade}</p>
              <button id="clearButton" onClick={clearButton}>
                Clear
              </button>
            </div>
          ) : (
            <p>No grade selected</p>
          )}
        </div>
      </section>

      <section className="history-section">
        <div className="container">
          <h2>History</h2>
          <ul>
            {history.map((item, index) => (
              <li key={index}>
                <span className="timestamp">{item.timestamp} - </span>
                <span>{item.action}:</span>
                <span className="grade-details">
                  {item.grade.name} ({item.grade.facultyNumber}): {item.grade.subject} - {item.grade.grade}
                </span>
              </li>
            ))}
          </ul>
        </div>
      </section>

      <footer>
        <p>© 2023 DSS Grade Management System.</p>
      </footer>
    </div>
  );
};
export default GradeManagementSystem;
